                        icon3: 'icon-jiaozi3',
                        icon4: 'icon-jiaozi4',
                        icon5: 'icon-jiaozi5',
                        name: '交子会员',
                        color: '#cb9d3e',
                        bgClass: 'jiaozi-bg'
                    },
                    '1': {
                        icon: 'icon-tongbao',
                        icon1: 'icon-tongbao1',
                        icon2: 'icon-tongbao2',
                        icon3: 'icon-tongbao3',
                        icon4: 'icon-tongbao4',
                        icon5: 'icon-tongbao5',
                        name: '通宝会员',
                        color: '#577f96',
                        bgClass: 'tongbao-bg'
                    },
                    '0': {
                        icon: 'icon-wuzhu',
                        icon1: 'icon-wuzhu1',
                        icon2: 'icon-wuzhu2',
                        icon3: 'icon-wuzhu3',
                        icon4: 'icon-wuzhu4',
                        icon5: 'icon-wuzhu5',
                        name: '五铢会员',
                        color: '#70a18a',
                        bgClass: 'wuzhu-bg'
                    }
                },
                info: {
                    "id": '',
                    "openid": "",
                    "level": '0',
                    "mobile": "",
                    "username": "",
                    "id_number": "",
                    "birthday": "",
                    "card_number": "",
                    "head_portrait": null,
                    "Interest": null,
                    "occupation": null,
                    "is_marriage": '',
                    "login_number": null,
                    "addtime": '',
                    "updatetime": '',
                    "login_time": null,
                    "status": '',
                    "gender": '',
                    "province": null,
                    "city": null,
                    "address": null,
                    "master_fiscal": null,
                    "join_time": null,
                    "last_time": null,
                    "maturity_time": null,
                    "investment_num": null,
                    "performance": null,
                    "activity_num": null,
                    "log_time": null,
                    "nickname": "",
                    "avatar": ""
                },
                isFinishInfo: true,
                receptionDisabled: false
            }
        },
        methods: {
            getInfo() {
                const _self = this;
                this.$ajax.get('/index/user/getinformation', {params: {sign: localStorage['sign']}}).then(res => {
                    if (res.data.code == 1001) {
                        alert(res.data.msg)
                        return
                    }
                    _self.info = res.data.data
                    //console.log(_self.info)
                    if (!_self.info.head_portrait || _self.info.head_portrait == '') {
                        _self.info.head_portrait = 'data:image/jpeg;base64,'
                    }else if(_self.info.head_portrait.indexOf('http') === -1){ //数据返回没有'http://',微信头像带有'http://'
                        _self.info.head_portrait = 'http://' + _self.info.head_portrait
                    }
                })
            },
            uploadPhoto(e) {
                let _self = this
                // 利用fileReader对象获取file
                let file = e.target.files[0];
                let filesize = file.size;
                let filename = file.name;
                // 2,621,440   2M
                if (filesize > 2101440) {
                    // 图片大于2MB
                }
                let reader = new FileReader();
                reader.readAsDataURL(file);
                reader.onload = function (e) {
                    // 读取到的图片base64 数据编码 将此编码字符串传给后台即可
                    let imgcode = e.target.result;
                    //console.log(imgcode.split(',')[1])
                    _self.uploadAction(imgcode.split(',')[1])
                }
            },
            uploadAction(imgStr){
                let data = {head_portrait:imgStr}
                this.$ajax.post('/index/user/update_head_portrait',data,{params: {sign: localStorage['sign']}}).then(res => {
                    this.toastMsg = res.data.msg
                    this.toast = true
                    if(res.data.code === 0){
                        this.getInfo()
                    }
                })
            }
        },
        created() {
            this.getInfo()
        },
        filters: {
            formateDate(v, fmt) {
                let D = new Date(v * 1000);
                var o = {
                    "M+": D.getMonth() + 1,                 //月份
                    "D+": D.getDate(),                    //日
                    "h+": D.getHours(),                   //小时
                    "m+": D.getMinutes(),                 //分
                    "s+": D.getSeconds(),                 //秒
                    "q+": Math.floor((D.getMonth() + 3) / 3), //季度
                    "S": D.getMilliseconds()             //毫秒
                };
                if (/(Y+)/.test(fmt))
                    fmt = fmt.replace(RegExp.$1, (D.getFullYear() + "").substr(4 - RegExp.$1.length));
                for (var k in o)
                    if (new RegExp("(" + k + ")").test(fmt))
                        fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
                return fmt;
            }
        }
    }
</script>

